package com.syntax.class001;

public class PrimitaveDataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// storing and representing numeric values

		byte box1 = 127;
		short box2 = 32767;
		int box3 = 100000; // most popular
		long box4 = 5465154545435L; // credit cards, transaction ids
		float variable1 = 1.99F;
		double variable2 = 999.999; //popular

		//storing single character
		
		char container='A';
		char dollar = '$';
		
		//storing yes or no values
		boolean hot=true;
		boolean tired=false;
		
		System.out.println(box1);
		System.out.println(variable2);
		System.out.println("variable2");
		System.out.println(box1 + box2);
		System.out.println(box4 * box3);
		System.out.println(hot);
		System.out.println(tired);
		System.out.println();
	}
}
